INSERT INTO xiaoyublog_blog VALUES('76','linuxé˜²ç«å¢™è®¾ç½\®','1497070782','<h3 id=\"h3--linux-\"><a name=\"ä¸€ã€Linuxé˜²ç«å¢™æ“ä½\œ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>ä¸€ã€Linuxé˜²ç«å¢™æ“ä½\œ</h3><h4 id=\"h4-1-\"><a name=\"1.é‡å¯åç”Ÿæ•\ˆ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>1.é‡å¯åç”Ÿæ•\ˆ</h4><pre><code class=\"lang-bash\">#å¼€å¯ï¼š \r\nchkconfig iptables on \r\n#å…³é—­ï¼\š \r\nchkconfig iptables off\r\n</code></pre>\r\n<h4 id=\"h4-2-\"><a name=\"2.å³æ—¶ç”Ÿæ•ˆ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>2.å³æ—¶ç”Ÿæ•ˆ</h4><pre><code class=\"lang-bash\">#å¼€å¯ï¼š \r\nservice iptables start \r\n#å…³é—­ï¼\š \r\nservice iptables stop\r\n</code></pre>\r\n<h4 id=\"h4-3-\"><a name=\"3.é…ç½®æ–‡ä»¶\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>3.é…ç½®æ–‡ä»¶</h4><pre><code class=\"lang-bash\">vim /etc/sysconfig/iptables\r\n</code></pre>\r\n<p>ä¿®æ”¹/etc/sysconfig/iptables æ–‡ä»¶ï¼Œæ·»åŠ ä»¥ä¸‹å†…å®¹ï¼š </p>\r\n<pre><code class=\"lang-bash\">-A RH-Firewall-1-INPUT -m state --state NEW -m tcp -p tcp --dport 80 -j ACCEPT å¼€å\¯80ç«¯å£è®¿é—®\r\n-A RH-Firewall-1-INPUT -m state --state NEW -m tcp -p tcp --dport 22 -j ACCEPT #å¼€å\¯22ç«¯å£è®¿é—®\r\n-A INPUT -m state â€“state NEW -m tcp -p tcp â€“dport 443 -j ACCEPT #å¼€å\¯443ç«¯å£è®¿é—®\r\n# åœ\¨-A RH-Firewall-1-INPUT -m state â€“state NEW -m tcp -p tcp â€“dport 22 -j ACCEPTå‰åŠ #å·æ³¨é‡Šæ‰ï¼Œå³å¯å±è”½é»˜è®¤çš„22ç«¯å£è®¿é—®\r\n</code></pre>\r\n<p>é‡å¯é˜²ç«å¢™ä½¿å¾—é…ç½®æ–‡ä»¶ç”Ÿæ•\ˆ</p>\r\n<pre><code class=\"lang-bash\">/etc/init.d/iptables restart\r\n</code></pre>\r\n<h3 id=\"h3--centos7-\"><a name=\"äºŒã€CentOS7 ä¸‹é˜²ç«å¢™çš„æ“ä½\œ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>äºŒã€CentOS7 ä¸‹é˜²ç«å¢™çš„æ“ä½\œ</h3><p>CentOS 7.0é»˜è®¤ä½¿ç”¨çš„æ˜¯firewallä½œä¸ºé˜²ç«å¢™ï¼Œè¿™é‡Œæ”¹ä¸ºiptablesé˜²ç«å¢™æ­¥éª¤ã€\‚</p>\r\n<h4 id=\"h4-1-firewall\"><a name=\"1.å…³é—­firewall\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>1.å…³é—­firewall</h4><pre><code class=\"lang-bash\">systemctl stop firewalld.service #åœæ­¢firewall\r\nsystemctl disable firewalld.service #ç¦æ­¢firewallå¼€æœºå¯åŠ\¨\r\nfirewall-cmd --state #æŸ¥çœ‹é»˜è®¤é˜²ç«å¢™çŠ¶æ€ï¼ˆå…³é—­åæ˜¾ç¤ºnotrunningï¼Œå¼€å¯åæ˜¾ç¤ºrunningï¼\‰\r\n</code></pre>\r\n<h4 id=\"h4-2-iptables-iptables-\"><a name=\"2.iptablesé˜²ç«å¢™ï¼ˆè¿™é‡Œiptableså·²ç»å®‰è£…ï¼Œä¸‹é¢è¿›è¡Œé…ç½®ï¼‰\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>2.iptablesé˜²ç«å¢™ï¼ˆè¿™é‡Œiptableså·²ç»å®‰è£…ï¼Œä¸‹é¢è¿›è¡Œé…ç½®ï¼‰</h4><pre><code class=\"lang-bash\">vi/etc/sysconfig/iptables #ç¼–è¾‘é˜²ç«å¢™é…ç½®æ–‡ä»\¶\r\n\r\n# sampleconfiguration for iptables service\r\n# you can edit thismanually or use system-config-firewall\r\n# please do not askus to add additional ports/services to this default configuration\r\n*filter\r\n:INPUT ACCEPT [0:0]\r\n:FORWARD ACCEPT[0:0]\r\n:OUTPUT ACCEPT[0:0]\r\n-A INPUT -m state--state RELATED,ESTABLISHED -j ACCEPT\r\n-A INPUT -p icmp -jACCEPT\r\n-A INPUT -i lo -jACCEPT\r\n-A INPUT -p tcp -mstate --state NEW -m tcp --dport 22 -j ACCEPT\r\n-A INPUT -p tcp -m state --state NEW -m tcp --dport 80 -jACCEPT\r\n-A INPUT -p tcp -m state --state NEW -m tcp --dport 8080-j ACCEPT\r\n-A INPUT -j REJECT--reject-with icmp-host-prohibited\r\n-A FORWARD -jREJECT --reject-with icmp-host-prohibited\r\nCOMMIT\r\n:wq! #ä¿å­˜é€€å‡\º\r\n</code></pre>\r\n<p>å¤‡æ³¨ï¼šè¿™é‡Œä½¿ç”\¨80å’\Œ8080ç«¯å£ä¸ºä¾‹ã€\‚<em>*</em>éƒ¨åˆ†ä¸€èˆ¬æ·»åŠ åˆ°â€\œ-A INPUT -p tcp -m state â€”state NEW -m tcpâ€”dport 22 -j ACCEPTâ€è¡Œçš„ä¸Šé¢æˆ–è€…ä¸‹é¢ï¼Œåˆ‡è®°ä¸è¦æ·»åŠ åˆ°æœ€åä¸€è¡Œï¼Œå¦åˆ™é˜²ç«å¢™é‡å¯åä¸ç”Ÿæ•ˆã€\‚</p>\r\n<pre><code class=\"lang-bash\">systemctlrestart iptables.service #æœ€åé‡å¯é˜²ç«å¢™ä½¿é…ç½®ç”Ÿæ•\ˆ\r\nsystemctlenable iptables.service #è®¾ç½®é˜²ç«å¢™å¼€æœºå¯åŠ\¨\r\n</code></pre>\r\n<h3 id=\"h3--ubuntu-\"><a name=\"ä¸‰ã€Ubuntuä¸‹é˜²ç«å¢™çš„æ“ä½\œ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>ä¸‰ã€Ubuntuä¸‹é˜²ç«å¢™çš„æ“ä½\œ</h3><p>LInuxåŸå§‹çš„é˜²ç«å¢™å·¥å…·iptablesç”±äºè¿‡äºç¹ç,æ‰€ä»¥ubuntuç³»ç»Ÿé»˜è®¤æä¾›äº†ä¸€ä¸ªåŸºäºiptableä¹‹ä¸Šçš„é˜²ç«å¢™å·¥å…·ufwã€‚è€ŒUFWæ”¯æŒå›¾å½¢ç•Œé¢æ“ä½œ,åªéœ€åœ¨å‘½ä»¤è¡Œè¿è¡Œufwå‘½ä»¤å³èƒ½çœ‹åˆ°ä¸€ç³»åˆ—çš„æ“ä½œã€\‚</p>\r\n<h4 id=\"h4-1-\"><a name=\"1.å®‰è£…ä¸å¸è½\½\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>1.å®‰è£…ä¸å¸è½\½</h4><p>å®‰è£…</p>\r\n<pre><code class=\"lang-bash\">apt-get install iptables\r\napt-get install ufw\r\n</code></pre>\r\n<p>å¸è½½</p>\r\n<pre><code class=\"lang-bash\">apt-get remove iptables\r\napt-get remove ufw\r\n</code></pre>\r\n<h4 id=\"h4-2-\"><a name=\"2.å¼€å¯ä¸å…³é—­\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>2.å¼€å¯ä¸å…³é—­</h4><p>å…³é—­ubuntuçš„é˜²ç«å¢™ : <code>ufw disable</code><br>å¼€å¯ubuntuçš„é˜²ç«å¢™ : <code>ufw enable</code></p>\r\n<p><strong>å¯ç”¨ufw</strong><br><code>ufw enable</code><br><code>ufw default deny</code><br>\r\n<h4 id=\"h4-3-\"><a name=\"3.å¸¸ç”¨æ“ä½œ\" class=\"reference-link\"></a><span class=\"header-link octicon octicon-link\"></span>3.å¸¸ç”¨æ“ä½œ</h4><p><strong>å¼€å¯å’Œç¦ç”¨</strong></p>\r\n<pre><code class=\"lang-bash\">ufw allow \\|deny [service]\r\n</code></pre>\r\n<p><strong>æ‰“å¼€æˆ–å…³é—­æŸä¸ªç«¯å\£</strong></p>\r\n<p>ä¾‹å¦‚:</p>\r\n<pre><code class=\"lang-bash\">ufw allow smtpã€€#å…è®¸æ‰€æœ‰çš„å¤–éƒ¨IPè®¿é—®æœ¬æœºçš\„25/tcp (smtp)ç«¯å£\r\nufw allow 22/tcp #å…è®¸æ‰€æœ‰çš„å¤–éƒ¨IPè®¿é—®æœ¬æœºçš\„22/tcp (ssh)ç«¯å£\r\nufw allow 53 #å…è®¸å¤–éƒ¨è®¿é—®53ç«¯å£(tcp/udp)\r\nufw allow from 192.168.1.100 #å…è®¸æ­¤IPè®¿é—®æ‰€æœ‰çš„æœ¬æœºç«¯å£\r\nufw allow proto udp 192.168.0.1 port 53 to 192.168.0.2 port 53\r\nufw allow from 202.203.0.0 port 22 to 202.203.255.255 port 22\r\nufw deny smtp #ç¦æ­¢å¤–éƒ¨è®¿é—®smtpæœåŠ¡\r\nufw delete allow smtp #åˆ é™¤ä¸Šé¢å»ºç«‹çš„æŸæ¡è§„åˆ\™\r\n</code></pre>\r\n<p><strong>æŸ¥çœ‹é˜²ç«å¢™çŠ¶æ€\</strong></p>\r\n<pre><code class=\"lang-bash\">ufw status\r\n</code></pre>\r\n<p><strong>å…è®¸æŸç‰¹å®\š IP</strong></p>\r\n<pre><code class=\"lang-bash\">ufw allow from xxx.xxx.xx.xxx\r\n</code></pre>\r\n<p><strong>åˆ é™¤ smtp ç«¯å£çš„è®¸å\¯</strong></p>\r\n<pre><code class=\"lang-bash\">ufw delete allow smtp\r\nufw delete allow 22\r\nufw delete allow allow from 202.203.0.0 port 22 to 202.203.255.255 port 22\r\n</code></pre>\r\n<p><img src=\"http://media.xiaoyulive.top/media/img/ufw01.png\" alt=\"\">\r\n<p><img src=\"http://media.xiaoyulive.top/media/img/ufw02.png\" alt=\"\">\r\n<p><img src=\"http://media.xiaoyulive.top/media/img/ufw03.png\" alt=\"\">','','','1','37','blog','21','0','0','n','n','n','y','y','','','0','0,0');
